-- 基础设置表
-- 资金账户
CREATE TABLE AB_FUND_ACCOUNT  (
    ID BIGINT NOT NULL AUTO_INCREMENT,
    FUNDCODE BIGINT NOT NULL,
    FUNDNAME VARCHAR(255),
    SELFCODE VARCHAR(255),
    INTIALAMOUNT DECIMAL(10,2),
    CREATEDATE BIGINT,
    MODIFYDATE BIGINT,
    REMARKS VARCHAR(255),
    CONSTRAINT PK_AB_FUND_ACCOUNT PRIMARY KEY (ID)
);
-- 收款项目
CREATE TABLE AB_RECEIVE_PAY_ITEM  (
    ID BIGINT NOT NULL AUTO_INCREMENT,
    CODE VARCHAR(255) NOT NULL,
    RECEIVEPAYTYPE BIGINT NOT NULL,
    NAME VARCHAR(255),
    CREATEDATE BIGINT,
    MODIFYDATE BIGINT,
    REMARKS VARCHAR(255),
    ISCOMMISSION SMALLINT,
    CONSTRAINT PK_AB_RECEIVE_PAY_ITEM PRIMARY KEY (ID)
);
-- 收款项目类别
CREATE TABLE AB_INCOME_CATEGORY_ITEM  (
    ID BIGINT NOT NULL AUTO_INCREMENT,
    PARENTID BIGINT NOT NULL,
    CODE VARCHAR(255) NOT NULL,
    NAME VARCHAR(255),
    SELFCODE VARCHAR(255),
    CREATEDATE BIGINT,
    MODIFYDATE BIGINT,
    REMARKS VARCHAR(255),
    CONSTRAINT PK_AB_RECEIVE_PAY_ITEM PRIMARY KEY (ID)
);
-- 付款项目
CREATE TABLE AB_PAYMENT_ITEM  (
    ID BIGINT NOT NULL AUTO_INCREMENT,
    CODE VARCHAR(255) NOT NULL,
    PAYTYPE BIGINT NOT NULL,
    NAME VARCHAR(255),
    CREATEDATE BIGINT,
    MODIFYDATE BIGINT,
    REMARKS VARCHAR(255),
    CONSTRAINT PK_AB_PAYMENT_ITEM PRIMARY KEY (ID)
);
-- 支出项目类别
CREATE TABLE AB_PAYMENT_CATEGORY_ITEM  (
    ID BIGINT NOT NULL AUTO_INCREMENT,
    PARENTID BIGINT NOT NULL,
    CODE VARCHAR(255) NOT NULL,
    NAME VARCHAR(255),
    SELFCODE VARCHAR(255),
    CREATEDATE BIGINT,
    MODIFYDATE BIGINT,
    REMARKS VARCHAR(255),
    CONSTRAINT PK_AB_PAYMENT_CATEGORY_ITEM PRIMARY KEY (ID)
);
-- 部门信息
CREATE TABLE AB_GROUP  (
    ID BIGINT NOT NULL AUTO_INCREMENT,
    CODE VARCHAR(255) NOT NULL,
    NAME VARCHAR(255),
    SELFCODE VARCHAR(255),
    CREATEDATE BIGINT,
    MODIFYDATE BIGINT,
    REMARKS VARCHAR(255),
    CONSTRAINT PK_AB_GROUP PRIMARY KEY (ID)
);
-- 业务员
CREATE TABLE AB_SALESMAN  (
    ID BIGINT NOT NULL AUTO_INCREMENT,
    CODE VARCHAR(255) NOT NULL,
    NAME VARCHAR(255),
    PWD VARCHAR(255),
    ISENTERSYS SMALLINT,
    ENTRYDATE BIGINT,
    RESIGNDATE BIGINT,
    CREATEDATE BIGINT,
    MODIFYDATE BIGINT,
    REMARKS VARCHAR(255),
    CONSTRAINT PK_AB_GROUP PRIMARY KEY (ID)
);

-- 客户信息表
CREATE TABLE AB_COMPANY_CUSTOMER_INFO  (
    ID BIGINT NOT NULL AUTO_INCREMENT,
    CREATEDATE BIGINT,
    MODIFYDATE BIGINT,
    CUSTOMERCODE VARCHAR(255) NOT NULL,
    CUSTOMERNAME VARCHAR(255) NOT NULL,
    TEL VARCHAR(255),
    TRANSACTIONID VARCHAR(157),
    PHONE VARCHAR(157),
    CONTACTPERSON VARCHAR(50),
    NPADDRESS BIGINT;
    SELFENCODING VARCHAR(50),
    ADDRESS  VARCHAR(255),
    REMARKS1 VARCHAR(255),
    REMARKS2 VARCHAR(255),
    ENPCREDITCODE VARCHAR(255),
    ENPTAXFILINGPWD VARCHAR(255),
    LEGALNAME VARCHAR(255),
    LEGALPHONE VARCHAR(255),
    STAMPDUTY VARCHAR(255),
    LEGALCARD VARCHAR(255),
    INCOMETAX VARCHAR(255),
    SURTAX VARCHAR(255),
    BANK VARCHAR(255),
    NATURETAX VARCHAR(255),
    SETUPDATE VARCHAR(255),
    BANKCARD VARCHAR(255),
    THREEPROTOCOL VARCHAR(255),
    ENPTYPE VARCHAR(255),
    PAPERWORKNUM SMALLINT,
    SHAREHOLDERNUM SMALLINT,
    TAXOFFICE VARCHAR(255),
    SALESMAN VARCHAR(255),
    TaxFILINGDONE VARCHAR(255),
    BUSINESSSTARTDATE VARCHAR(255),
    ACCOUNTING VARCHAR(255),
    ISINVOIVING VARCHAR(255),
    TAXTYPE SMALLINT,
    EARLYCOLLECTION SMALLINT,
    CONSTRAINT PK_AB_COMPANY_CUSTOMER_INFO PRIMARY KEY (ID)
);
CREATE UNIQUE INDEX UDX_XY_USER_USERID ON HD_USER(ACCOUNT);
-- 银行卡表
CREATE TABLE XY_BANK_CARD  (
    ID BIGINT NOT NULL AUTO_INCREMENT,           
    CARDID BIGINT NOT NULL,
    NAME VARCHAR(50),
    USERID BIGINT,
    ACCOUNT_BANK VARCHAR(300),
    ISDEFAULT INTEGER,
    CONSTRAINT PK_XY_BANK_CARD PRIMARY KEY (ID)
);
CREATE UNIQUE INDEX UDX_XY_BANK_CARD ON XY_BANK_CARD(CARDID);
-- 公司
CREATE TABLE XY_COMPANY  (
    ID BIGINT NOT NULL AUTO_INCREMENT,           
    COMPANYNAME VARCHAR(32),
    PAYEE VARCHAR(50),
    BANKNAME VARCHAR(157),
    CARDID BIGINT,
    WECHATPAY VARCHAR(300),
    ALIPAY VARCHAR(300),
    TELEPHONE VARCHAR(300),
    ENABLED SMALLINT NOT NULL,
    STATUS VARCHAR(10),
    CONSTRAINT PK_XY_COMPANY PRIMARY KEY (ID)
);
-- 支付凭证
CREATE TABLE XY_PAYMENT_VOUCHER  (
    ID BIGINT NOT NULL AUTO_INCREMENT,           
    USERID BIGINT,
    CREATETIME BIGINT,
    CREATOR VARCHAR(50),
    VOUCHERURL VARCHAR(150),
    ISRECHARGE INTEGER NOT NULL,
    CONSTRAINT PK_XY_PAYMENT_VOUCHER PRIMARY KEY (ID)
);
ALTER TABLE XY_PAYMENT_VOUCHER ADD CONSTRAINT FK_PAYMENT_USER FOREIGN KEY (USERID) references XY_USER(ID);
-- 用户额度表
CREATE TABLE XY_USER_RECHARAGE_INFO  (
    ID BIGINT NOT NULL AUTO_INCREMENT,           
    ACCOUNT VARCHAR(32) NOT NULL,
    USERID BIGINT,
    USERNAME VARCHAR(32),
    QUOTA BIGINT,
    LEVEL INTEGER,
    COINNUM BIGINT,
    COINPRIVACE FLOAT,
    DIVIDENDS BIGINT,
    CASHDIVIDENDS BIGINT,
    CASHD BIGINT,
    CONSTRAINT PK_XY_USER_RECHARAGE_INFO PRIMARY KEY (ID)
);
-- 虚拟币维护
CREATE TABLE XY_COIN_INFO  (
    ID BIGINT NOT NULL AUTO_INCREMENT,           
    COINNAME VARCHAR(32),
    COINTYPE VARCHAR(32),
    COINPRICE FLOAT,
    ISENABLE BIGINT,
    CONSTRAINT PK_XY_COIN_INFO PRIMARY KEY (ID)
);
-- 等级维护
CREATE TABLE XY_LEVEL_INFO  (
    ID BIGINT NOT NULL AUTO_INCREMENT,           
    NAME VARCHAR(32),
    LEVEL INTEGER,
    LEVELNAME VARCHAR(32),
    RECHARGEPRICE BIGINT,
    RECHARGEMULTIPE FLOAT,
    RELEASEQUOTA INTEGER,
    ISENABLE BIGINT,
    CONSTRAINT PK_XY_LEVEL_INFO PRIMARY KEY (ID)
);